import React, { useState } from 'react';
import './App.css';
import logo from './Pictures/logo.png';
import { Button, Form, Container, Row, Col } from 'react-bootstrap';
import axios from "axios";

const ContactPage = () => {
  // Define state for form data
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    message: ""
  });

  // Handle form input changes
  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  // Handle form submission
  const handleSubmit = (event) => {
    event.preventDefault();
    // Make POST request to your backend
    axios.post('http://localhost:4000/api/contact', formData)
      .then(response => {
        alert('Message sent successfully!');
        console.log(response.data);
      })
      .catch(error => {
        console.error('Failed to send message:', error);
        alert('Failed to send message.');
      });

    // Optionally reset form after sending
    setFormData({
      firstName: "",
      lastName: "",
      email: "",
      message: ""
    });
  };

  return (
    <div>
      <nav className="navbar">
        <div className="logo">
          <img className="h-10 mr-3" style={{ width: '150px', height: '150px', padding: '5px' }} src={logo} alt="Logo" />
        </div>
        {/* Your navigation links here */}
      </nav>

      <Container className="contact-page">
        <Row>
          <Col>
            <div className="contact-details">
              <h2>Contact us.</h2>
              <p>info@soireevalet.com</p>
              <p>(800) 660-1906</p>
              <p>1470 Howard Street<br />San Francisco, Ca 94103</p>
              {/* Social media links here */}
            </div>
          </Col>
          <Col>
            <div className="contact-form">
              <Form onSubmit={handleSubmit}>
                <Form.Group>
                  <Form.Label>First Name (required)</Form.Label>
                  <Form.Control type="text" name="firstName" value={formData.firstName} onChange={handleChange} required />
                </Form.Group>
                <Form.Group>
                  <Form.Label>Last Name</Form.Label>
                  <Form.Control type="text" name="lastName" value={formData.lastName} onChange={handleChange} />
                </Form.Group>
                <Form.Group>
                  <Form.Label>Email (required)</Form.Label>
                  <Form.Control type="email" name="email" value={formData.email} onChange={handleChange} required />
                </Form.Group>
                <Form.Group>
                  <Form.Label>Message (required)</Form.Label>
                  <Form.Control as="textarea" name="message" value={formData.message} onChange={handleChange} required />
                </Form.Group>
                <Button type="submit">Send</Button>
              </Form>
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default ContactPage;
