import React, { useState } from 'react';
import './App.css';
import logo from './Pictures/logo.png';
import contactImage from "./Pictures/contactImage.jpg";
import { Button, Form, Container, Row, Col } from 'react-bootstrap';
import axios from 'axios';

const QuotePage = () => {
    // State to hold form data
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        company: '',
        country: 'United States',
        address1: '',
        address2: '',
        city: '',
        state: '',
        zip: '',
        email: '',
        phone: '',
        preferredContact: 'Email',
        eventDate: '',
        timePeriod: '',
        serviceRequested: '',
        numberOfGuests: '',
        numberOfCars: '',
        comments: ''
    });

    // Handle changes in form inputs
    const handleChange = (event) => {
        const { name, value } = event.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    // Handle form submission
    const handleSubmit = (event) => {
        event.preventDefault();
        // API call to backend server to submit the form data
        axios.post('http://localhost:4000/api/quotes', formData)
            .then(response => {
                alert('Quote request sent successfully!');
                console.log(response.data);
            })
            .catch(error => {
                console.error('Failed to send quote request:', error);
                alert('Failed to send quote request.');
            });
    };

    return (
        <div>
            <nav className="navbar">
                <div className="logo">
                    <img className="h-10 mr-3" style={{ width: '150px', height: '150px', padding: '5px' }} src={logo} alt="Logo" />
                </div>
                {/* Navigation links */}
            </nav>

            <Container className="contact-info-section">
                <Row>
                    <Col md={6}>
                        {/* Contact Information and Image */}
                    </Col>
                    <Col md={6}>
                        <Form onSubmit={handleSubmit}>
                            {/* Form fields and labels */}
                            <Button variant="primary" type="submit">Request a Quote</Button>
                        </Form>
                    </Col>
                </Row>
            </Container>
        </div>
    );
}

export default QuotePage;
